mapper.select:
SELECT distinct t.*,s.status_name,u.real_name FROM( SELECT p.* ,u.unit_name FROM `jiebao_jpms_punit` t ,jiebao_jpms_proposal p,jiebao_jpms_unit u WHERE t.proposal_id = p.proposal_id and t.unit_id = u.unit_id and p.status != -1 and t.type BETWEEN 3 and 4 and u.unit_id = #{unitId}) t ,jiebao_jpms_status s ,jiebao_jpms_user u WHERE t.`status` = s.id and t.user_id = u.user_id
and t.cause like concat('%', #{cause}, '%')
and t.proposal_id = #{status}
and t.create_time >= #{startDate}
and #{endDate} >= t.create_time
ORDER BY t.create_time desc
